import { Component, EventEmitter, Input, Output } from '@angular/core';

@Component({
  selector: 'app-breed',
  templateUrl: './breed.component.html',
  styleUrls: ['./breed.component.scss']
})

export class BreedComponent {
  @Input() breed: any;
  @Output() breedSelected = new EventEmitter();

  constructor() { }

  public selectBreed() {
    this.breedSelected.emit(this.breed.name);
  }
}
<mat-card>
  <mat-card-header>
    <mat-card-title>{{breed.breed }}</mat-card-title>
    <mat-card-subtitle>{{breed.country}}</mat-card-subtitle>
  </mat-card-header>
  <mat-card-content>
    <p>Origin: {{breed.origin || 'Unkown'}}</p>
    <p>Coat: {{breed.coat || 'Unkown'}}</p>
    <p>Pattern: {{breed.pattern || 'Unkown'}}</p>
  </mat-card-content>
</mat-card>
